    img[src="https://github.githubassets.com/assets/mona-loading-default-c3c7aad1282f.gif"] {
      content: url("https://giscus.catppuccin.com/assets/loading_48x48.gif");
    }

    /* Header when logged out */
    .HeaderMenu-link {
      color: @text;

      &:hover {
        color: @text;
      }
    }
    .header-search-button {
      &.placeholder {
