      }
      .ansifg-m {
        color: var(--color-ansi-magenta);
      }
      .ansifg-gr {
        color: var(--color-ansi-gray);
      }
    }

    .js-activity-overview-graph .js-highlight-blob {
      fill: @accent;
      stroke: @accent;
    }

    .subnav-link.selected {
      border-bottom-color: @accent;
    }

    /* Pull request check in progress indicator */
    [stroke="#DBAB0A" i] {
