    @accent: @catppuccin[@@flavor][@@accentColor];

    @shadow: if(@flavor = latte, #dbdfef, #010409);

    color-scheme: if(@flavor = latte, light, dark);

    ::selection {
      background-color: fade(@accent, 30%);
    }

    input,
    textarea {
      &::placeholder {
        color: @subtext0 !important;
      }
    }

    * {
      --text-color: @text;
      --color-base: @text;
      --color-bg: @crust;
      --color-secondary: @subtext0;
