      0px 2px 4px -1px fade(@base, 20%), 0px 4px 5px 0px fade(@base, 14%), 0px
      1px 10px 0px fade(@base, 12%);
    --dt-shadow-elevation-3:
      0px 5px 5px -3px fade(@base, 20%), 0px 8px 10px 1px fade(@base, 14%), 0px
      3px 14px 2px fade(@base, 12%);
    --dt-shadow-elevation-4:
      0px 5px 5px -3px fade(@base, 20%), 0px 8px 10px 1px fade(@base, 14%), 0px
      3px 14px 2px fade(@base, 12%);
    --dt-shadow-elevation-5:
      0px 8px 10px -6px fade(@base, 20%), 0px 16px 24px 2px fade(@base, 14%),
      0px 6px 30px 5px fade(@base, 12%);
    --dt-surface-container-lowest: @crust;
    --dt-surface-container-low: @mantle;
    --dt-surface-container: @surface0;
    --dt-surface-container-high: @surface1;
    --dt-surface-container-highest: @surface2;
    --dt-surface-dim: @crust;
    --dt-surface-bright: @base;
    --dt-on-primary-container: @text;
    --dt-primary-container-icon: @text;
    --dt-primary-container-link: @text;
    --dt-primary: @accent;
    --dt-primary-action: @accent;
    --dt-primary-action-stateful: @accent;
    --dt-primary-outline: @accent;
    --dt-primary-action-state-layer: @accent;
    --dt-primary-container: @surface0;
    --dt-on-primary: @base;
    --dt-primary-icon: @base;
    --dt-primary-link: @base;
