/* ==UserStyle==
@name           Google Accounts Tokyo Night (tokyonight)
@namespace      github.com/openstyles/stylus
@version        2.0
@description    Tokyo Night for Google Accounts
@author         JoshuaMarkle
==/UserStyle== */

@-moz-document domain("accounts.google.com") {
    :root {
        --bg-dark: #16161E;
        --bg-code: #12121a;
        --bg: #1A1B26;
        --bg-2: #282B3C;
        --bg-3: #3F4256;
        --bg-4: #444B6A;
        --highlight-1: #1E202E;
        --highlight-2: #2E3448;
        --blue-accent: #89ddff;
        --text: #c0caf5;
        --text-dark: #565f89;
        --cyan: #7dcfff;
        --blue: #7aa2f7;
        --blue-dark: #5C88E6;
        --orange: #ff9e64;
        --orange-dark: #e3a144;
        --red: #f7768e;
        --red-dark: #db4b4b;
        --green: #9ece6a;
        --green-dark: #1abc9c;
        --purple: #bb9af7;

        --gm3-sys-color-background: var(--bg);
        --gm3-sys-color-surface-container: var(--bg-dark);
        --gm3-sys-color-on-surface: var(--text);
        --gm3-sys-color-on-surface-variant: var(--text-dark);
        --gm3-sys-color-primary: var(--blue);
        --gm3-sys-color-outline-variant: var(--bg-2);

        --mdc-theme-surface: var(--bg);
        --mdc-elevation-overlay-color: var(--bg);
    }   

    * { border-color: var(--bg-2) !important; }

    /* Google Popup Accounts */
    #container > div > div,
    html, #container { background: var(--bg-dark); }
    #container > div > div > div { background: var(--bg); }
    p { color: var(--text-dark) !important; }
    #dialog_banner,
    #dialog_heading,
    #credentials-picker > div > div > div > div:first-child { color: var(--text); }
    #credentials-picker > div > div > div > div { color: var(--text-dark); }
    #credentials-picker > div:hover,
    #credentials-picker > div:focus,
    #credentials-picker > div:active { background: var(--bg-2); }
    a { color: var(--blue) !important; }
}

/* ==UserStyle==
@name GitHub Catppuccin
@namespace github.com/catppuccin/userstyles/styles/github
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/github
@version 2025.02.04.1
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/github/catppuccin.user.less
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Agithub
@description Soothing pastel theme for GitHub
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@-moz-document /* Match every page on GitHub except github.com/home, github.com/features/*, and github.com/organizations/plan. */
regexp(
    "https:\/\/github\.com(?!(\/home$|\/features($|\/.*)|\/organizations\/plan)).*$"
  ),
  domain("gist.github.com"),
  domain("docs.github.com"),
  domain("viewscreen.githubusercontent.com") {
  [data-color-mode="auto"] {
    @media (prefers-color-scheme: light) {
      &[data-light-theme="light"] {
        #catppuccin(@lightFlavor);
      }
      &[data-light-theme="dark"] {
        #catppuccin(@darkFlavor);
      }
    }

    @media (prefers-color-scheme: dark) {
      &[data-dark-theme="light"] {
        #catppuccin(@lightFlavor);
      }
      &[data-dark-theme="dark"] {
        #catppuccin(@darkFlavor);
      }
    }
  }
  [data-color-mode="light"][data-light-theme="dark"],
  [data-color-mode="dark"][data-dark-theme="dark"] {
    #catppuccin(@darkFlavor);
  }
  [data-color-mode="light"][data-light-theme="light"],
  [data-color-mode="dark"][data-dark-theme="light"] {
    #catppuccin(@lightFlavor);
  }

  #catppuccin(@flavor) {
    @rosewater: @catppuccin[@@flavor][@rosewater];
    @flamingo: @catppuccin[@@flavor][@flamingo];
    @pink: @catppuccin[@@flavor][@pink];
    @mauve: @catppuccin[@@flavor][@mauve];
    @red: @catppuccin[@@flavor][@red];
    @maroon: @catppuccin[@@flavor][@maroon];
    @peach: @catppuccin[@@flavor][@peach];
    @yellow: @catppuccin[@@flavor][@yellow];
    @green: @catppuccin[@@flavor][@green];
    @teal: @catppuccin[@@flavor][@teal];
    @sky: @catppuccin[@@flavor][@sky];
    @sapphire: @catppuccin[@@flavor][@sapphire];
    @blue: @catppuccin[@@flavor][@blue];
    @lavender: @catppuccin[@@flavor][@lavender];
    @text: @catppuccin[@@flavor][@text];
    @subtext1: @catppuccin[@@flavor][@subtext1];
    @subtext0: @catppuccin[@@flavor][@subtext0];
    @overlay2: @catppuccin[@@flavor][@overlay2];
    @overlay1: @catppuccin[@@flavor][@overlay1];
    @overlay0: @catppuccin[@@flavor][@overlay0];
    @surface2: @catppuccin[@@flavor][@surface2];
    @surface1: @catppuccin[@@flavor][@surface1];
    @surface0: @catppuccin[@@flavor][@surface0];
