      color: @accent;
    }

    // dropdown icon
    .multiselect__select::before {
      border-color: @text transparent transparent;
      color: @text;
    }
    // header button hover
    .btn-wrapper:hover {
      a,
      button {
        --tw-brightness: none !important;
      }
    }
    .nuxt-loading-indicator {
      background: @accent !important;
    }
  }
}

