      --white: @mantle;
      --white-a06: @surface0;
      --white-a12: @surface0;
      --white-a18: @surface0;
      --white-a25: @surface0;
      --white-a30: @mantle;
      --white-a40: @mantle;
      --white-a45: @mantle;
      --white-a55: @mantle;
      --white-a60: @subtext0;
      --white-a70: @mantle;
      --white-a75: @mantle;
      --white-a85: @mantle;
      --white-a90: @mantle;
      --white-a100: @mantle;

      --warm-gray-10: @base;
      --warm-gray-20: @base;
      --warm-gray-30: @base;
      --warm-gray-40: @base;
      --warm-gray-50: @base;
      --warm-gray-60: @base;
      --warm-gray-70: @base;
      --warm-gray-80: @base;
      --warm-gray-90: @base;
      --warm-gray-100: @base;
      --warm-gray-60-a10: fade(@base, 10%);
      --warm-gray-60-a15: fade(@base, 15%);
      --warm-gray-60-a20: fade(@base, 20%);
      --warm-gray-60-a25: fade(@base, 25%);
      --warm-gray-60-a30: fade(@base, 30%);
      --warm-gray-60-a35: fade(@base, 35%);
    }

    .share-promoted-detour-button-legacy {
      background-color: @accent !important;
    }
  }
}

